mapper.update:
update board
set content=#{content}
where nickname=#{nickname} and lock_post='a'

update board
set cate='nocate'
where email=#{email} and cate=#{catename}

update category
set catename=#{cate}
where idx=#{idx}

update board
set cate=#{cate}
where nickname=#{nickname} and cate=#{oldcate}

update board
set title=#{title}, content=#{content}, lock_post=#{lock_post}, cate=#{cate}
where idx=#{idx}

mapper.select:
select count(*) as listCnt
from board
where nickname=#{nickname}

select count(*)
from board
where nickname=#{nickname} and cate=#{cate}

select count(*)
from board
where nickname=#{nickname} and cate=#{cate}

mapper.update:
update category
set catecnt=#{catecnt}
where email=#{email} and catename=#{cate}

mapper.select:
select cate
from board
where idx=#{idx}

mapper.update:
update category
set catecnt=catecnt-1
where email=#{email} and catename=#{catename}

mapper.select:
select email
from user
where nickname=#{nickname}

mapper.update:
update board
set hit=hit+1
where idx=#{idx}

update guestbook
set content=#{content}
where idx=#{idx}
